public class Combination { public static void main(String[] args){  combine("abc"); }  private static void combine(String str){  char[] input = str.toCharArray();  int length = input.length;  StringBuffer output = new StringBuffer();  doCombine(input, output, length, 0);  System.out.println("End "+output.toString()); }  private static void doCombine(char[] in, StringBuffer out, int length, int start){  for(int i = start; i < length; i++){   out.append(in[i]);   if(i < length - 1){    doCombine(in, out, length, i+1);   }   out.setLength(out.length() - 1);  } }}